mapper.select:
SELECT RN , PJTNO , PJTNM , COMPANYNM , TO_CHAR(PJTFROMDATE,'YYYY-MM-DD') AS PJTFROMDATE , TO_CHAR(PJTTODATE, 'YYYY-MM-DD') AS PJTTODATE FROM(
SELECT ROW_NUMBER() OVER (ORDER BY PJTNO DESC) AS RN , P.PJTNO AS PJTNO , P.PJTNM AS PJTNM , DC3.DTL_CDNM AS COMPANYNM , P.PJTFROMDATE AS PJTFROMDATE , P.PJTTODATE AS PJTTODATE FROM EX00.PROJECT P JOIN DTL_CODE DC3 ON P.COMPYNO = DC3.DTL_CD AND DC3.CODE = 'D600'
AND P.PJTNM LIKE '%' || #{pjtNm} || '%'
AND P.COMPYNO LIKE '%' || #{compyNo} || '%'
AND P.PJTFROMDATE BETWEEN TO_DATE(#{pjtFromDate}, 'YYYY-MM-DD') and TO_DATE(#{pjtToDate}, 'YYYY-MM-DD')
AND P.PJTFROMDATE > = #{pjtFromDate}
AND P.PJTTODATE < = #{pjtToDate}
AND 1 = 0
) M WHERE RN BETWEEN ((#{pageNum} - 1) * #{amount}) + 1 AND #{pageNum} * #{amount}

mapper.update:
  UPDATE EX00.PROJECT
  	SET 
  		PJTNM = #{pjtNm}
  		, PJTFROMDATE = TO_DATE(#{pjtFromDate}, 'YYYY-MM-DD')
  		, PJTTODATE = TO_DATE(#{pjtToDate}, 'YYYY-MM-DD')
  		, COMPYNO =#{compyNo}
  	WHERE
		PJTNO = #{pjtNo}

mapper.insert:
INSERT INTO PROJECT (
	PJTNO
	, PJTNM
	, PJTFROMDATE
	, PJTTODATE
	, COMPYNO
	)
VALUES (
	project_seq.nextval
	, #{pjtNm}
	, #{pjtFromDate}
	, #{pjtToDate}
	, #{compyNo}
	)
